import { Component } from '@angular/core';
import { BookService } from './book.service';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css']
})
export class BookComponent {
  searchQuery: string = '';
  books: any[] = []; 
  filteredBooks: any[] = [];

  constructor(private bookService: BookService) {}

  ngOnInit() {
    this.bookService.getBooks().subscribe(books => {
      this.books = books;
      this.filterBooks(); 
    });
  }

  filterBooks() {
    this.filteredBooks = this.books.filter(book =>
      book.title.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
      book.author.toLowerCase().includes(this.searchQuery.toLowerCase())
    );
  }

  onSearchChange(event: any) {
    this.searchQuery = event.target.value;
    this.filterBooks();
  }

}
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<title>Modern Table with Button and Search</title>
</head>
<body>

<div class="search-container">
  <input type="text" id="searchInput"  [(ngModel)]="searchQuery" placeholder="Search...">
  <ul>
    <li *ngFor="let book of filteredBooks">
      {{ book.title }} by {{ book.author }}
    </li>
  </ul>
</div>

<div class="table-container">
  <table class="modern-table" id="dataTable">
    <thead>
      <tr>
        <th>Column 1</th>
        <th>Column 2</th>
        <th>Column 3</th>
        <th>Column 4</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td>Data 1</td>
        <td>Data 2</td>
        <td>Data 3</td>
        <td>Data 4</td>
        <td><button class="action-button">Action</button></td>
      </tr>
      <!-- Add more rows as needed -->
    </tbody>
  </table>
</div>

<script>
  // Set the width of the search input to match the table
  window.onload = function() {
    var table = document.getElementById('dataTable');
    var searchInput = document.getElementById('searchInput');
    searchInput.style.width = table.offsetWidth + 'px';
  };
</script>

</body>
</html>
