import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AuthenticationService } from '../login/authentication.service';

@Component({
  selector: 'app-user-profile',
  templateUrl: './user-profile.component.html',
  styleUrls: ['./user-profile.component.css']
})
export class UserProfileComponent implements OnInit{
  books: any[] = [];
  userData: any;

  constructor(private http: HttpClient, private authenticationService: AuthenticationService) { }

  ngOnInit(): void {
    this.userData = this.authenticationService.userData;
    this.http.get<any[]>('http://localhost:3000/borrows/1/borrows')
      .subscribe(data => {
        this.books = data; 
    }, error => {
      console.error('Error fetching data:', error);
    });
    console.log(this.userData);
  }


  returnBook(userId: number, borrowId: number) {
    this.http.post<any>(`http://localhost:3000/borrows/${userId}/return/${borrowId}`, {})
      .subscribe(
        response => {
          console.log('Book returned successfully:', response);
        },
        error => {
          console.error('Error returning book:', error);
        }
      );
  }

  borrowBook(userId: number, bookId: number) {
    this.http.post<any>(`http://localhost:3000/borrows/${userId}/${bookId}`, {})
      .subscribe(
        response => {
          console.log('Book borrowed successfully:', response);
        },
        error => {
          console.error('Error borrowing book:', error);
        }
      );
  }




}
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<title>Centered Div Layout</title>
</head>
<body>



    <div class="menu">
        <div>
          <a href="#home">Home</a>
          <a href="#borrowed">Borrowed Books</a>
        </div>
        <div>
          <a href="#books">Books List</a>
        </div>
      </div>

  <div class="container">
    <div class="first-div">
      <div class="user-info">
          <img src="assets/user-avatar.jpg" alt="User Avatar" class="user-avatar">
          <div class="user-details">
              <div class="user-name">{{userData.payload.username}}</div>
              <div class="user-age">Email: {{userData.payload.email}}</div>
              <div class="user-books">
                  <span class="borrowed-books"></span>
                  <span class="returned-books"></span>
              </div>
          </div>
      </div>
  </div>

  <div class="second-div">
    <h2>Borrowed Books</h2>
    <ul class="book-list">
        <ng-container *ngFor="let book of books">
            <li *ngIf="book.book.isAvailable !== null">
                <div class="book-info">
                    <div class="book-title">{{ book.book.title }}</div>
                    <div class="book-author">Author: {{ book.book.author }}</div>
                    <div class="dates">
                        <div class="book-author">Borrow Date: {{ book.borrowDate }}</div>
                        <div class="book-author">Return Date: {{ book.returnDate }}</div>
                    </div>

                </div>

                <button class="book-button" *ngIf="book.book.isAvailable"  (click)="borrowBook(userData.payload.userId, book.book.id)">Renew</button>
            </li>
        </ng-container>
    </ul>
</div>


<div class="third-div">
    <h2>Books List</h2>
    <ul class="book-list">
        <ng-container *ngFor="let book of books">
            <li *ngIf="book.returnDate === null">
                <div class="book-info">
                    <div class="book-title">{{ book.book.title }}</div>
                    <div class="book-author">{{ book.book.author }}</div>
                </div>
                <button class="book-button" (click)="returnBook(userData.payload.userId, book.id)">Return</button>
            </li>
        </ng-container>
    </ul>
</div>



</div>
</body>
</html>
