import { Component } from '@angular/core';
import { BookService } from './book.service';
import { AuthenticationService } from '../login/authentication.service';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css']
})
export class BookComponent {
  searchQuery: string = '';
  books: any[] = []; 
  filteredBooks: any[] = [];
  userData: any;


  constructor(private bookService: BookService, private authenticationService: AuthenticationService) {}

  ngOnInit() {
    this.userData = this.authenticationService.userData;
    this.bookService.getBooks().subscribe(books => {
      this.books = books;
      this.filterBooks(); 
    });
  }

  filterBooks() {
    this.filteredBooks = this.books.filter(book =>
      book.title.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
      book.author.toLowerCase().includes(this.searchQuery.toLowerCase())
    );
  }

  onSearchChange(event: any) {
    this.searchQuery = event.target.value;
    this.filterBooks();
  }

  borrowBook(userId: number, bookId: number) {
    this.bookService.borrowBook(userId,bookId);
  }



}
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
</head>
<body>

    <div class="container">
<div class="search-container">
  <input type="text" id="searchInput"  (input)="onSearchChange($event)" [(ngModel)]="searchQuery"  placeholder="Search...">
</div>

<div class="table-container">
  <table class="modern-table" id="dataTable">
    <thead>
      <tr>
        <th>Title</th>
        <th>Author</th>
        <th>Publication Date</th>
        <th>Available</th>
        <th>Borrow</th>
      </tr>
    </thead>
    <tbody>
      <tr  *ngFor="let book of filteredBooks">
        <td>{{book.title}}</td>
        <td>{{book.author}}</td>
        <td>{{book.publicationDate}}</td>
        <td>{{book.isAvailable}}</td>
        <td><button class="action-button" *ngIf="book.isAvailable"  (click)="borrowBook(userData.payload.userId, book.book.id)">Borrow</button></td>
      </tr>
    </tbody>
  </table>
</div>
</div>
<script>
  window.onload = function() {
    var table = document.getElementById('dataTable');
    var searchInput = document.getElementById('searchInput');
    searchInput.style.width = table.offsetWidth + 'px';
  };
</script>

</body>
</html>
